mapper.select:
select
ifnull((select enumvaluename from enumtable b where a.feetype=b.enumvalueid and b.enumtype='2006'),'') as
feetypeenumvaluename,
ifnull((select enumvaluename from enumtable b where a.paytype=b.enumvalueid and b.enumtype='1011'),'') as
paytypeenumvaluename,
ifnull((select name from transline b where a.lineid=b.id),'') as lineidname,
ifnull((select boxsize from boxs b where a.boxtype=b.id),'') as boxtypeboxsize,
ifnull((select enumvaluename from enumtable b where a.nodetype=b.enumvalueid and b.enumtype='2004'),'') as
nodetypeenumvaluename,
a.*
from feeinfo a


        `id` = #{id}



        and `parent_id` = #{parentId}



        and `code` = #{code}



        and `name` = #{name}



        and `feetype` = #{feetype}



        and `paytype` = #{paytype}



        and `lineid` = #{lineid}



        and `boxtype` = #{boxtype}



        and `price` = #{price}



        and `cost` = #{cost}



        and `remark` = #{remark}



        and `status` = #{status}



        and `nodetype` = #{nodetype}



        and `uptdate` = #{uptdate}




    limit #{offset}, #{limit}
